import React, { Component } from "react";

const team = {
	name: 'Dodgers',
	city: 'Los Angeles'
}

const playerList = [
	{
    name: 'Trevor Bauer',
    number: 27,
    position: 'Pitcher'
  },
  {
    name: 'Justin Turner',
    number: 10,
    position: '3rd Base'
  },
  {
    name: 'Sandy Koufax',
    number: 32,
    position: 'Pitcher'
  },
  {
    name: 'Mookie Betts',
    number: 50,
    position: 'Right Field'
  },

]

const displayPlayerList = playerList.map((c, idx) => {
	return(
			<h3 key={idx}>{c.name}{c.number}{c.position}</h3>
			)
		})


class FavoriteTeam extends Component {
  render() {
    return (
      <section className="hero is-fullheight is-default is-bold">
        <div  title="image55" className="hero-body">
          <div className="container has-text-centered">
            <div className="columns is-vcentered">
              <div className="column is-5">
                <figure className="image is-4by3">
                  <img 
                    src="https://picsum.photos/700/500/?random"
                    alt="Description"
                  />
                </figure>
              </div>
              <div className="column is-6 is-offset-1">
                <h1 className="title is-2">{team.name}, {team.city}</h1>
                <h2 className="subtitle is-4">
                {displayPlayerList}
                   </h2>
                <br />
                <p className="has-text-centered">
                  <a className="button is-medium is-info is-outlined">
                  </a>
                </p>
              </div>
            </div>
          </div>
        </div>
        <div className="hero-foot">
          <div className="container">
            <div className="tabs is-centered">
              <ul>
                <li>
                  <a>And this is the bottom</a>
                </li>
              </ul>
            </div>
          </div>
        </div>
      </section>
    );
  }
}

export default FavoriteTeam;
